function pgquery(){
    return{
        select: function(what) {
            return "select "+what + " ";
        }
        ,
        from : function(table) {
            return "from "+ table + " ";
        },
        innerjoin : function(table1, table2, attribute ) {
            return table1 + " INNER JOIN "+table2+" ON "+table1+"."+attribute+" = "+table2+ "."+attribute+" "
        },
        where : function(expression) {
            return "where " + expression;
        }
    }
}